// generated by react-router-hono-server/dev
import { createHonoServer } from "react-router-hono-server/bun";
import { initializeLogger, logger } from "~/lib/logger/logger";

// Initialize LogTape only in production (development uses entry.server.tsx)
if (process.env.NODE_ENV === 'production') {
  await initializeLogger();
}

const server = await createHonoServer();

// Log server startup only in production
if (process.env.NODE_ENV === 'production') {
  const port = process.env.PORT || 3000;
  const nodeEnv = process.env.NODE_ENV || 'development';

  logger.info('Starting Abaqus Job Manager server', 'SERVER_STARTUP', {
    port,
    environment: nodeEnv,
    bunVersion: Bun.version,
    timestamp: new Date().toISOString()
  });

  logger.info('Server started successfully', 'SERVER_READY', {
    port,
    environment: nodeEnv,
    pid: process.pid
  });
}

// Handle graceful shutdown (production only)
const gracefulShutdown = (signal: string) => {
  if (process.env.NODE_ENV === 'production') {
    logger.info('Received shutdown signal', 'SERVER_SHUTDOWN', {
      signal,
      uptime: process.uptime(),
      timestamp: new Date().toISOString()
    });
  }
  
  process.exit(0);
};

process.on('SIGTERM', () => gracefulShutdown('SIGTERM'));
process.on('SIGINT', () => gracefulShutdown('SIGINT'));

export default server;
